var getFileList = function getFileList(s3, bucket, prefix) {
  return new Promise(function (resolve, reject) {
    var params = {
      Bucket: bucket,
      Prefix: prefix
    };
    s3.listObjectsV2(params, function (err, data) {
      if (err) {
        reject(err);
      } else {
        resolve(data.Contents.map(function (file) {
          return file.Key;
        }));
      }
    });
  });
};

module.exports = getFileList;
